import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './Navbar';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000/api';

const AttendanceDashboard = () => {
  const [attendance, setAttendance] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [dateFilter, setDateFilter] = useState('');

  useEffect(() => {
    fetchAttendance();
  }, []);

  const fetchAttendance = async (search = '', date = '') => {
    try {
      setLoading(true);
      const params = {};
      if (search) params.search = search;
      if (date) params.date = date;
      
      const response = await axios.get(`${API_BASE_URL}/attendance`, { params });
      setAttendance(response.data);
    } catch (error) {
      console.error('Error fetching attendance:', error);
      alert('Error fetching attendance data. Please check if the backend is running.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    fetchAttendance(value, dateFilter);
  };

  const handleDateFilter = (e) => {
    const value = e.target.value;
    setDateFilter(value);
    fetchAttendance(searchTerm, value);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this attendance record?')) {
      try {
        await axios.delete(`${API_BASE_URL}/attendance/${id}`);
        fetchAttendance(searchTerm, dateFilter);
        alert('Record deleted successfully!');
      } catch (error) {
        console.error('Error deleting record:', error);
        alert('Error deleting record.');
      }
    }
  };

  const clearFilters = () => {
    setSearchTerm('');
    setDateFilter('');
    fetchAttendance();
  };

  return (
    <div className="app">
      <Navbar />
      <div className="main-content">
        <div className="dashboard-container">
          <div className="dashboard-header">
            <h2 className="dashboard-title">Attendance Dashboard</h2>
            <div className="controls">
              <input
                type="text"
                placeholder="Search by name or ID..."
                value={searchTerm}
                onChange={handleSearch}
                className="search-input"
              />
              <input
                type="date"
                value={dateFilter}
                onChange={handleDateFilter}
                className="date-filter"
              />
              {(searchTerm || dateFilter) && (
                <button onClick={clearFilters} className="cta-button">
                  Clear Filters
                </button>
              )}
            </div>
          </div>

          {loading ? (
            <div className="loading">Loading attendance data...</div>
          ) : attendance.length === 0 ? (
            <div className="empty-state">
              <h3>No attendance records found</h3>
              <p>Start by marking some attendance records.</p>
            </div>
          ) : (
            <div className="table-container">
              <table className="attendance-table">
                <thead>
                  <tr>
                    <th>Employee Name</th>
                    <th>Employee ID</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {attendance.map((record) => (
                    <tr key={record.id}>
                      <td>{record.employeename}</td>
                      <td>{record.employeeid}</td>
                      <td>{new Date(record.date).toLocaleDateString()}</td>
                      <td>
                        <span className={`status-${record.status.toLowerCase()}`}>
                          {record.status}
                        </span>
                      </td>
                      <td>
                        <button
                          onClick={() => handleDelete(record.id)}
                          className="delete-button"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AttendanceDashboard;
